const ScrollableContent = () => {
  const avatars = [
    {
      id: 1,
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 2,
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 3,
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS1jIqcLjdvSDxQv0YjHHzBoZGoTbtGc6RvsP_07-reJ9ebBEoXJlKg7N_cHA&s",
    },
    {
      id: 4,
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 5,
      image:
        "data:image/jpeg;base64,",
    },
    {id: 6, image: "https://via.placeholder.com/64"},
    {
      id: 7,
      image:
        "data:image/jpeg;base64,",
    },
  ];

  const images = [
    {
      id: 1,
      src: "data:image/jpeg;base64,",
    },
    {
      id: 2,
      src: "data:image/jpeg;base64,",
    },
    {
      id: 3,
      src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRHsi2KtzQb-h4xhb6ktRciiv8xR-lSM7srbiZcwOqvoBqj2kYjDgTyDlBpVyAjjUcJsGA&usqp=CAU",
    },
    {
      id: 4,
      src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSaZRBOKi_tNmVE9c_WLFCVt5HCo1DQ_sQ-HZINtiCBfcf3Go4gFNQGmZWSIPbd5hqRl9Q&usqp=CAU",
    },
    {
      id: 5,
      src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRcc99a9l3NMHLihqiVNScx-42ojSTkWPuDMPnxpxts3YbKOvf8BjKYJy-IN3Yn9F6Vn_U&usqp=CAU",
    },
    {
      id: 56,
      src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSFdRc3A-vlMNQJE6bbBAnxZweICZJ9gmUXQjV--IGynfF1EtUYKp48MzSjVB508u3uPhw&usqp=CAU",
    },
  ];

  return (
    <div className="max-w-sm mx-auto">
      <div className="flex overflow-x-auto mb-4 scrollbar-hide">
        {avatars.map((avatar) => (
          <div key={avatar?.id} className="flex-shrink-0 w-16 h-16 bg-gray-300 rounded-full mr-4">
            <img
              src={avatar?.image}
              alt="Avatar"
              className="w-full h-full object-cover rounded-full"
            />
          </div>
        ))}
      </div>
      <div className="overflow-y-auto h-64 scrollbar-hide">
        {images.map((image) => (
          <div key={image?.id} className="mb-4">
            <img src={image?.src} alt="Image" className="w-full h-32 object-cover" />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ScrollableContent;
